                }
        	}else {
                msg.payload = 4; //// 4 is off optional
            }
        }

        ///////FALL/SPRING DAY///////
        if (context.ToD_today == 3) { // fall/spring
        	if (context.temp1 > context.weath){ //If its colder outside than inside
                if (context.temp1 < context.thermostat){ //If its colder than thermostat set point inside (thermostat is on)
        		    //If its warmer in the living room (temp2) 
                    msg.payload = 3; //// 3 is hot outside open open door (let warm air from living room/kitchen in)
                } else if (context.temp >= context.thermostat){ //(colder in the living room)
                    //Close door (isolate space and heat up faster)
        		    msg.payload = 0; //// 0 is cold outside close Close door (isolate space and heat up faster)
                } else {
                    msg.payload = 4; //// 4 is off optional
                }
        	} else{
        	    msg.payload = 4;//// 4 is off optional
        	}
        }

    }

    context.temp1_bool = false;
    context.weath_bool = false;
    context.motion_bool = false;
    context.sunrise_bool = false;
    context.sunset_bool = false;
    context.timenow_bool = false;
